import React from "react";
import PropTypes from "prop-types";
import AppBar from "@material-ui/core/AppBar";
import CssBaseline from "@material-ui/core/CssBaseline";
import Divider from "@material-ui/core/Divider";
import Drawer from "@material-ui/core/Drawer";
import Hidden from "@material-ui/core/Hidden";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import IconButton from "@material-ui/core/IconButton";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import MailIcon from "@material-ui/icons/Mail";
import MenuIcon from "@material-ui/icons/Menu";
import Toolbar from "@material-ui/core/Toolbar";
import { Link } from "react-router-dom";
import { Container, Box } from "@material-ui/core";
import Autocomplete from "@material-ui/lab/Autocomplete";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from "@material-ui/pickers";

import {
  HomeRounded,
  AddRounded,
  ShoppingBasketRounded,
  HistoryRounded,
  PowerOffRounded,
  CardGiftcardRounded,
} from "@material-ui/icons";
import Typography from "@material-ui/core/Typography";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import { TextField } from "@material-ui/core";
import MaterialUIPickers from "./datePcikerComponent";
import OfferTable from "./offerTable";

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  drawer: {
    [theme.breakpoints.up("sm")]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up("sm")]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up("sm")]: {
      display: "none",
    },
  },
  // necessary for content to be below app bar
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

function Offers(props) {
  const { window } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };
  const [selectedDate, setSelectedDate] = React.useState(
    new Date("2014-08-18T21:11:54")
  );

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <Divider />
      <List>
        <ListItem button key="Home" component={Link} to="./dashboard">
          <ListItemIcon>
            <HomeRounded />
          </ListItemIcon>
          <ListItemText primary="Home" />
        </ListItem>
      </List>
      <List>
        <ListItem button key="add_item" component={Link} to="./addItems">
          <ListItemIcon>
            <AddRounded />
          </ListItemIcon>
          <ListItemText primary="Add Item" />
        </ListItem>
      </List>
      <List>
        <ListItem button key="order" component={Link} to="./orders">
          <ListItemIcon>
            <ShoppingBasketRounded />
          </ListItemIcon>
          <ListItemText primary="Orders" />
        </ListItem>
      </List>
      <List>
        <ListItem button key="past_order" component={Link} to="./past_orders">
          <ListItemIcon>
            <HistoryRounded />
          </ListItemIcon>
          <ListItemText primary="Past Order" />
        </ListItem>
      </List>
      <List>
        <ListItem button key="offers" component={Link} to="./offers">
          <ListItemIcon>
            <CardGiftcardRounded />
          </ListItemIcon>
          <ListItemText primary="Offers" />
        </ListItem>
      </List>
      <Divider />

      <List>
        <ListItem button key="logout" component={Link} to="./logout">
          <ListItemIcon>
            <PowerOffRounded />
          </ListItemIcon>
          <ListItemText primary="Log Out" />
        </ListItem>
      </List>
    </div>
  );

  const container =
    window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" noWrap>
            Verbose
          </Typography>
        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        {/* The implementation can be swapped with js to avoid SEO duplication of links. */}
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === "rtl" ? "right" : "left"}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better open performance on mobile.
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <main className={classes.content}>
        <div className={classes.toolbar} />
        <Typography variant="h4">Offers</Typography>
        <Container maxWidth="md">
          <Box
            bgcolor=""
            textAlig="center"
            p="24px"
            mt="50px"
            boxShadow="2"
            borderRadius="15px"
          >
            <form>
              <Box display="flex" p="16px">
                <TextField
                  id="outlined-basic"
                  label="Offer Name"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />

                <TextField
                  id="des_per"
                  label="Discount Percent"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />
                <TextField
                  id="des"
                  label="Description"
                  variant="outlined"
                  fullWidth
                  margin="normal"
                />
              </Box>
              <Box display="flex" p="16px">
                <TextField
                  fullWidth
                  margin="normal"
                  id="start_date"
                  label="Start Date"
                  type="date"
                  variant="outlined"
                  defaultValue="2017-05-24"
                  className={classes.textField}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
                <TextField
                  fullWidth
                  margin="normal"
                  id="end_date"
                  label="End Date"
                  type="date"
                  variant="outlined"
                  defaultValue="2017-05-24"
                  className={classes.textField}
                  InputLabelProps={{
                    shrink: true,
                  }}
                />
              </Box>
              <Box display="flex" p="16px">
                <Autocomplete
                  id="sub_cat"
                  options={top100Films}
                  getOptionLabel={(option) => option.title}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Select category"
                      variant="outlined"
                    />
                  )}
                  fullWidth
                  margin="normal"
                />
                <Autocomplete
                  id="item"
                  options={items}
                  getOptionLabel={(option) => option.title}
                  renderInput={(params) => (
                    <TextField
                      {...params}
                      label="Select Item"
                      variant="outlined"
                    />
                  )}
                  fullWidth
                  margin="normal"
                />
              </Box>

              <Button
                variant="contained"
                color="primary"
                fullWidth
                margin="normal"
              >
                Add Offer
              </Button>
            </form>
          </Box>
          <Box mt="50px">
            <Typography mt="50px" variant="h4">
              Past Offers
            </Typography>
            <OfferTable />
          </Box>
        </Container>
      </main>
    </div>
  );
}

Offers.propTypes = {
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};
const top100Films = [
  { title: "Fast Casual" },
  { title: "Main Course" },
  { title: "Desert" },
  { title: "Cold drink" },
  { title: "Bread" },
  { title: "Chiness" },
];
const items = [
  { title: "Mango Shake" },
  { title: "Juice" },
  { title: "Chocolate Shake" },
  { title: "Bannana Shake" },
  { title: "Kitkat Shake" },
  { title: "Oreo Shake" },
];

export default Offers;
